mapper.insert:
INSERT INTO
detail(
  title
  ,periodStart
  ,periodEnd
  ,endCompany
  ,topCompany
  ,industry
  ,topic
  ,participantPeple
  ,workingStyle
  ,station
  ,commutingTime
  ,commutingTimeHour
  ,commutingTimeMinute
  ,participantDate
  ,mainTechnology
  ,database
  ,position
  ,fullSituation
  ,futurePlan
  ,customerTrend
  ,helthCondition
  ,consultation
  ,other
  ,createAt
  ,updateAt
)
VALUES(
  CONCAT(
    '業務報告書 '
    ,to_char(#{periodStart},'yyyy')
    ,'年'
    ,to_char(#{periodStart},'mm')
    ,'月'
    ,to_char(#{periodStart},'dd')
    ,'日 ～ '
    ,to_char(#{periodEnd},'yyyy')
    ,'年'
    ,to_char(#{periodEnd},'mm')
    ,'月'
    ,to_char(#{periodEnd},'dd')
    ,'日'
  )
  ,#{periodStart}
  ,#{periodEnd}
  ,#{endCompany}
  ,#{topCompany}
  ,#{industry}
  ,#{topic}
  ,#{participantPeple}
  ,#{workingStyle}
  ,#{station}
  ,#{commutingTime}
  ,#{commutingTimeHour}
  ,#{commutingTimeMinute}
  ,#{participantDate}
  ,#{mainTechnology}
  ,#{database}
  ,#{position}
  ,#{fullSituation}
  ,#{futurePlan}
  ,#{customerTrend}
  ,#{helthCondition}
  ,#{consultation}
  ,#{other}
  ,CURRENT_TIMESTAMP
  ,CURRENT_TIMESTAMP
)

mapper.update:
UPDATE detail SET
  title =
    CONCAT(
      '業務報告書 '
      ,to_char(#{periodStart},'yyyy')
      ,'年'
      ,to_char(#{periodStart},'mm')
      ,'月'
      ,to_char(#{periodStart},'dd')
      ,'日 ～ '
      ,to_char(#{periodEnd},'yyyy')
      ,'年'
      ,to_char(#{periodEnd},'mm')
      ,'月'
      ,to_char(#{periodEnd},'dd')
      ,'日'
    )
  ,periodStart = #{periodStart}
  ,periodEnd = #{periodEnd}
  ,endCompany = #{endCompany}
  ,topCompany = #{topCompany}
  ,industry = #{industry}
  ,topic = #{topic}
  ,participantPeple = #{participantPeple}
  ,workingStyle = #{workingStyle}
  ,station = #{station}
  ,commutingTime = #{commutingTime}
  ,commutingTimeHour = #{commutingTimeHour}
  ,commutingTimeMinute = #{commutingTimeMinute}
  ,participantDate = #{participantDate}
  ,mainTechnology = #{mainTechnology}
  ,database = #{database}
  ,position = #{position}
  ,fullSituation = #{fullSituation}
  ,futurePlan = #{futurePlan}
  ,customerTrend = #{customerTrend}
  ,helthCondition = #{helthCondition}
  ,consultation = #{consultation}
  ,other = #{other}
  ,updateAt = CURRENT_TIMESTAMP
WHERE
  reportId = #{reportId}

UPDATE workdetail SET
  workName = #{workName}
  ,situation = #{situation}
  ,issuesProblems = #{issuesProblems}
  ,updateAt = CURRENT_TIMESTAMP
WHERE
  workDetailId = #{workDetailId} AND
  reportId = #{reportId}
